mapper.update:
update md_question
set md_question_title = #{md_question_title}
	, md_question_content = #{md_question_content}
	, md_question_type = #{md_question_type}
where md_question_id = #{md_question_id}

mapper.select:
	select md_question_id, md_id, member_id, member_nick, md_question_title, md_question_content, md_question_date, md_question_type
    from md_question
	where md_question_id = #{md_question_id}
